import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Header extends React.Component {
  render() {
    const { email, total, gastos } = this.props;
    const cot = parseFloat(gastos[0]?.exchangeRates[[gastos[0].currency]].ask);
    const num = parseFloat(gastos[0]?.value);
    const a = gastos || [];
    console.log(a);
    if (a.length !== 0) {
      const valortotal = a?.reducer((ant, at) => (
        ant + parseFloat(at?.exchangeRates[[at.currency]].ask)
	  * parseFloat(at?.value)
		  ), 0);
		  console.log(cot, num, valortotal);
    }

    // const valorTotal =
    return (
      <header>
        <span data-testid="email-field">{email}</span>
        <span>Despesa Total </span>
        <span data-testid="total-field">{total?.toFixed(2) || 0}</span>
        <span data-testid="header-currency-field">BRL</span>
      </header>
    );
  }
}

const mapStateToProps = (state) => ({
  email: state.user.email,
  total: state.wallet.ask,
  gastos: state.wallet.expenses,
});

Header.propTypes = {
  email: PropTypes.string.isRequired,
  gastos: PropTypes.string.isRequired,
  total: PropTypes.string.isRequired,
};

export default connect(mapStateToProps)(Header);
