import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import { addSpent, fetchCurrencies } from '../actions';

const INITIAL_STATE = {
  value: '',
  description: '',
  currency: '',
  method: '',
  tag: '',
  exchangeRates: {},
};

class Wallet extends React.Component {
   state=INITIAL_STATE

   componentDidMount() {
     this.fetchAPI();
   }

    fetchAPI = async (moeda = '') => {
      const { getcurrencies } = this.props;
      const { valor } = this.state;
      console.log(valor);
      const currencie = await getcurrencies(moeda, valor);
      return currencie;
    }

    handleChange = ({ target }) => {
      const { name, value } = target;
      this.setState({ [name]: value });
    }

    handleSubmit = (e) => {
      const { setSpent } = this.props;
      const { moeda } = this.state;
      e.preventDefault();
      //   console.log(dispatch);
      this.fetchAPI(moeda, valor);
      setSpent(this.state);
      this.setState(INITIAL_STATE);
    }

    render() {
      const { email, currencies } = this.props;
      //   console.log(currencies);
      const { valor, descricao, moeda, metodoPagamento, tag } = this.state;
      return (
        <div>

          <Header />
          <div>
            <form onSubmit={ this.handleSubmit }>
              <label htmlFor="valor">
                Valor
                <input
                  data-testid="value-input"
                  name="valor"
                  value={ valor }
                  onChange={ this.handleChange }
                  id="valor"
                />
              </label>
              <label htmlFor="moeda">
                Moeda
                <select
                  role="combobox"
                  id="moeda"
                  value={ moeda }
                  onChange={ (e) => this.setState({ moeda: e.target.value }) }
                >
                  {currencies && currencies.map((el, index) => (
                    <option key={ el + index }>{el}</option>
                  ))}
                </select>
              </label>

              <select
                value={ metodoPagamento }
                onChange={ (e) => this.setState({ metodoPagamento: e.target.value }) }
                data-testid="method-input"
              >

                <option>Dinheiro</option>
                <option>Cartão de crédito</option>
                <option>Cartão de débito</option>

              </select>

              <select
                value={ tag }
                onChange={ (e) => this.setState({ tag: e.target.value }) }
                data-testid="tag-input"
              >
                <option>Alimentação</option>
                <option>Lazer</option>
                <option>Trabalho</option>
                <option>Transporte</option>
                <option>Saúde</option>

              </select>

              <label htmlFor="descricao">

                <input
                  data-testid="description-input"
                  name="descricao"
                  value={ descricao }
                  onChange={ this.handleChange }
                  id="descricao"
                />
              </label>

              <button
                type="submit"
              >
                Adicionar despesa

              </button>
            </form>
          </div>
          <p>{email}</p>
        </div>
      );
    }
}

const mapStateToProps = (state) => ({
  email: state.user.email,
  currencies: state.wallet.currencies,
});

const mapDispatchToProps = (dispatch) => ({
  getcurrencies: (moeda, valor) => { dispatch(fetchCurrencies(moeda, valor)); },
  setSpent: (spent) => { dispatch(addSpent(spent)); },
});

Wallet.propTypes = {
  email: PropTypes.string.isRequired,
  getcurrencies: PropTypes.func.isRequired,
  setSpent: PropTypes.func.isRequired,
  currencies: PropTypes.node.isRequired,

};

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);
