import React from 'react';

const QTD_CHARACTERES = 6;

class Home extends React.Component {
    validButton = () => {
      const { email, senha } = this.state;
      if (email && senha.length >= QTD_CHARACTERES) {
        return false;
      }
    }

    render() {
	  return (
        <div>
    <form>

            <label htmlFor="email-home">
        Email
              <input
                name="email"
                type="email"
                data-testid="email-input"
                id="email-home"
              />

      </label>
            <label htmlFor="senha-home">
        senha
              <input
                name="senha"
                type="password"
                data-testid="password-input"
                id="senha-home"
              />

      </label>
            <button
        type="submit"
        disabled={ this.validButton }
      >
        Entrar

      </button>
          </form>
  </div>
	  );
    }
}

export default Home;
